----------------------------------------------------------------------------------------------------
-- image-string conversion functions

local function hexdump_to_string(hexdump)
	local str = ""
	for n = 1, hexdump:len(), 2 do
		str = str .. string.char("0x" .. hexdump:sub(n,n+1))
	end
	return str
end

local function init()	

	-- Fixes bug that does not calculate the width and height correctly, when the lua is activated from the command line
	-- For this, init must be executed after calling emu.register
	width = emu.screenwidth() 
	height = emu.screenheight()

	for nFightstick = 1, 2 do
		-- Read Images
		img_fightstick_base[nFightstick] = gd.createFromPngStr(hexdump_to_string(png_fightstick_base[nFightstick])):gdStr()
		img_stick_back[nFightstick] = gd.createFromPngStr(hexdump_to_string(png_stick_back[nFightstick])):gdStr()
		img_stick_center[nFightstick] = gd.createFromPngStr(hexdump_to_string(png_stick_center[nFightstick])):gdStr()

		img_stick[nFightstick] = {}

		for i=1 , #png_stick[nFightstick]  do  
			img_stick[nFightstick][i] = gd.createFromPngStr(hexdump_to_string(png_stick[nFightstick][i])):gdStr()	
		end
	end

	-- Init dataInputsPlayer

	for n = 1, 2 do
		local base_x = (n-1)*(width-84)
		local base_y = height - 32
		local button1_x = base_x + 40
		local button1_y = base_y + 5
		local stick_x = base_x+2
		local stick_y = base_y+2

		dataInputsButtonsPlayer[n] =
		{-- input buttons , x-image , y-image	
		{"P" .. n .. " Weak Punch", button1_x, button1_y+2},
		{"P" .. n .. " Medium Punch", button1_x+14, button1_y},
		{"P" .. n .. " Strong Punch", button1_x+28, button1_y},
		{"P" .. n .. " Weak Kick", button1_x-2, button1_y+14},
		{"P" .. n .. " Medium Kick", button1_x+12, button1_y+12},
		{"P" .. n .. " Strong Kick", button1_x+26, button1_y+12},	
		}

		dataInputsStickPlayer[n] =
		{-- inputs vector , x-image , y-image, id image pressed
		{{"P" .. n .. " Up", "P" .. n .. " Right"}, stick_x, stick_y, 5},
		{{"P" .. n .. " Right","P" .. n .. " Down"}, stick_x, stick_y, 6},
		{{"P" .. n .. " Down", "P" .. n .. " Left"}, stick_x, stick_y, 7},
		{{"P" .. n .. " Left", "P" .. n .. " Up"}, stick_x, stick_y, 8},
		{{"P" .. n .. " Up"}, stick_x, stick_y, 1},
		{{"P" .. n .. " Right"}, stick_x, stick_y, 2},
		{{"P" .. n .. " Down"}, stick_x, stick_y, 3},
		{{"P" .. n .. " Left"}, stick_x, stick_y, 4}
		}
	end
end

