-- Show Fightstick in Visual mode
local function Fightstick_Visual_Show( nFightstick )

	-- init is executed only 1 time
	if bInitHasBeenInitialized==false then
		-- Fixes bug that does not calculate the width and height correctly, when the lua is activated from the command line
		-- For this, init must be executed after calling emu.register
		init()
		bInitHasBeenInitialized = true
	end
	
	-- Display stick
	for i,dataInputs in pairs(dataInputsStickPlayer) do -- dataInputsPlayer Array

		-- coordinates stick center
		local stick_x = dataInputs[1][2]
		local stick_y = dataInputs[1][3]
		local base_x = stick_x - 2
		local base_y = stick_y - 2

		-- Display Fightstick base
		gui.gdoverlay(base_x, base_y, img_fightstick_base[nFightstick],0.8)
		--gui.box(base_x, base_y,base_x+80, base_y+29,color1,color2)


		-- Display stick back
		gui.gdoverlay(stick_x, stick_y, img_stick_back[nFightstick])
		
		isNeutralStickInput = true
		for j,dataInput in pairs(dataInputs) do -- dataInputs Array

			local isPressedInput = true		

			for k,input in pairs(dataInput[1]) do -- Inputs array
				isPressedInput = isPressedInput and joypad.get()[input]				
			end

			if isPressedInput then
				-- select id image pressed or release
				gui.gdoverlay(dataInput[2], dataInput[3], img_stick[nFightstick][dataInput[4]])
				isNeutralStickInput = false
				break 
						
			end
		end

		if isNeutralStickInput then
			gui.gdoverlay(stick_x, stick_y, img_stick_center[nFightstick])
		end
	end

	
	-- Display buttons
	for i,dataInputs in pairs(dataInputsButtonsPlayer) do -- dataInputsPlayer Array

		for j,dataInput in pairs(dataInputs) do -- dataInputs Array
		
			-- select id image pressed or release
			local id_image = joypad.get()[dataInput[1]] and 9 or 10

			gui.gdoverlay(dataInput[2], dataInput[3], img_stick[nFightstick][id_image])						
		end
	end
end

