-- Show Fightstick in text mode
local t_color = { 
		on1  = 0xFF0000FF,
		on2  = 0x000000FF,
		off1 = 0xFFFFFFFF,
		off2 = 0x000000FF
	}


local function Fightstick_Text_Show()	

	local t_entrada = {}
	local width = emu.screenwidth()	
	local stick_y = emu.screenheight()

	for n = 1, 2 do
		local stick_x = (n-1)*(width-62)-45
		t_entrada[n .. "^"] =  {stick_x + 55 , stick_y - 18, "P" .. n .. " Up"}
		t_entrada[n .. "v"] =  {stick_x + 55 , stick_y - 12, "P" .. n .. " Down"}
		t_entrada[n .. "<"] =  {stick_x + 49 , stick_y - 15, "P" .. n .. " Left"}
		t_entrada[n .. ">"] =  {stick_x + 61 , stick_y - 15, "P" .. n .. " Right"}
		t_entrada[n .. "LP"] = {stick_x + 75 , stick_y - 19, "P" .. n .. " Weak Punch"}
		t_entrada[n .. "MP"] = {stick_x + 85,  stick_y - 19, "P" .. n .. " Medium Punch"}
		t_entrada[n .. "HP"] = {stick_x + 95,  stick_y - 19, "P" .. n .. " Strong Punch"}
		t_entrada[n .. "LK"] = {stick_x + 75 , stick_y - 11, "P" .. n .. " Weak Kick"}
		t_entrada[n .. "MK"] = {stick_x + 85,  stick_y - 11, "P" .. n .. " Medium Kick"}
		t_entrada[n .. "HK"] = {stick_x + 95,  stick_y - 11, "P" .. n .. " Strong Kick"}
	end

	for k,v in pairs(t_entrada) do
		local color1,color2 = t_color.on1,t_color.on2
		if joypad.get()[v[3]] == false  then color1,color2 = t_color.off1,t_color.off2 end
		gui.text(v[1], v[2], string.sub(k, 2), color1, color2)
	end
	
end

function lib.Fightstick_Show()

	if 0< FightstickDisplaySelector and FightstickDisplaySelector <3 then
		Fightstick_Visual_Show(FightstickDisplaySelector)		
	elseif FightstickDisplaySelector ==3 then
		Fightstick_Text_Show()	
	end
end

----------------------------------------------------------------------------------------------------
-- hotkey function
----------------------------------------------------------------------------------------------------
local optionMenu={"Disabled", "Yzkof", "MadCatz", "Text"}
local optionMenuLength = #optionMenu

function lib.EnableFightstickDisplay(incrementCounter)

	FightstickDisplaySelector = FightstickDisplaySelector + incrementCounter

	if FightstickDisplaySelector >= optionMenuLength then
		FightstickDisplaySelector = 0
	elseif FightstickDisplaySelector < 0 then
		FightstickDisplaySelector = optionMenuLength-1
	end

	return optionMenu[FightstickDisplaySelector+1];
end

return lib
